// Your code goes here
const body = document.getElementsByTagName("body");
const bodyArr = Array.from(body);

const footer = document.getElementsByTagName("footer");
const footerArr = Array.from(footer);

const img = document.getElementsByTagName("img");
const imgArr = Array.from(img);

const section = document.getElementsByTagName("section");
const sectionArr = Array.from(section);

const a = document.getElementsByTagName("a");
const aArr = Array.from(a);

const h2 = document.getElementsByTagName("h2");
const h2Arr = Array.from(h2);

const p = document.getElementsByTagName("p");
const pArr = Array.from(p);

const home = document.getElementsByClassName("home");
const homeArr = Array.from(home);

const logo = document.getElementsByClassName("logo-heading");
const logoArr = Array.from(logo);

const btn = document.getElementsByClassName('btn');
const btnArr = Array.from(btn);

function beep() {
    var snd = new Audio("data:audio/wav;base64,");  
    snd.play();
}

aArr.forEach((a) => {
    a.addEventListener("click", () => {
        console.log('clicked link');
        event.preventDefault();
    });
});

h2Arr.forEach((h2) => {
    h2.addEventListener("dblclick", () => {
        h2.style.margin = "-5em";
    });
});

btnArr.forEach((btn) => {
    btn.addEventListener('dblclick', () => {
        btn.style.borderRadius = '100%';
        event.stopPropagation();
    });
});

logoArr[0].addEventListener('mouseenter', () => {
    beep();
});

logoArr[0].addEventListener('mouseleave', () => {
    beep();
});

homeArr[0].addEventListener("click", () => {
  bodyArr[0].style.transform = "scale(0.1)";
  bodyArr[0].style.transition = "all 600s";
});

footerArr[0].addEventListener("click", () => {
  bodyArr[0].style.transform = "scale(2)";
  bodyArr[0].style.transition = "all 0.5s";
});

pArr.forEach((p) => {
    p.addEventListener('copy', () => {
        p.style.display = "none";
    });
});

// Window Events
window.addEventListener('focus', () => {
    sectionArr.forEach((section) => {
        section.style.transform = 'skew(30deg, 20deg)';
    });
});

window.addEventListener("blur", () => {
  pArr.forEach(p => {
    p.style.color = "red";
  });
  imgArr.forEach(img => {
    img.style.transform = "rotate(180deg)";
  });
  bodyArr[0].style.transform = "rotate(20030deg)"
  bodyArr[0].style.transition = "10s";
});

window.addEventListener('resize', () => {
    pArr.forEach((p) => {
        p.style.transform = "rotate(700deg)";
        p.style.transition = "1s";
    });
});

window.addEventListener('scroll', () => {
  bodyArr[0].style.transform = "rotate(0deg)"
});

window.addEventListener('keypress', () => {
    homeArr[0].style.filter = "hue-rotate(9000deg)";
    homeArr[0].style.transition = "60s";
});
